(207, 1, '2013-03-17 11:03:51', '2013-03-17 02:03:51', '/*\r\n作者：dfc643\r\n来自：湖南涉外经济学院~计科1203\r\n博客：http://www.fcsys.tk/webapp/wordpress\r\n日期：2013-03-16\r\n[***************组长：邓方成***************]\r\n*/\r\nusing namespace std;\r\n#include "stdafx.h"\r\n#include&lt;iostream.h&gt;\r\n#include&lt;stdlib.h&gt;\r\n#include&lt;windows.h&gt;\r\n\r\ntypedef struct //学生类型结构体变量\r\n{\r\nchar cName[12];\r\nchar cStuNum[10];\r\ndouble dChinese,dMath,dEnglish,dVC;\r\ndouble dAvg,dTotal;\r\n}STUDENT;\r\n\r\nint nStuNum=0; //学生人数\r\nSTUDENT sStu[150]; //学生结构体数组\r\n\r\nvoid fINPUT(); //声明表列\r\nvoid fSORT();\r\nvoid fOUTPUT();\r\nvoid fEXIT();\r\n\r\nvoid fSHOW_MENU() //菜单函数\r\n{\r\n__MENUSTART__:\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\ncout&lt;&lt;" 欢迎使用学生成绩管理系统"&lt;&lt;endl;\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\ncout&lt;&lt;" [1] 输入学生各科成绩"&lt;&lt;endl;\r\ncout&lt;&lt;" [2] 排序学生平均成绩"&lt;&lt;endl;\r\ncout&lt;&lt;" [3] 输出学生最终成绩"&lt;&lt;endl;\r\ncout&lt;&lt;" [-] ----------------"&lt;&lt;endl;\r\ncout&lt;&lt;" [4] 退出学生成绩系统"&lt;&lt;endl;\r\nint nMenu_Item=0;\r\ncout&lt;&lt;"\\n*请选择一项操作以继续[1-4]：";\r\ncin&gt;&gt;nMenu_Item; //用户输入选项\r\nswitch(nMenu_Item) //判断用户输入并调用相应函数\r\n{\r\ncase 1:fINPUT();break;\r\ncase 2:fSORT();break;\r\ncase 3:fOUTPUT();break;\r\ncase 4:fEXIT();break;\r\ndefault:goto __MENUSTART__; //若不对重新显示菜单\r\n}\r\n}\r\nvoid fINPUT() //输入学生成绩\r\n{\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\ncout&lt;&lt;" 输入学生各科成绩"&lt;&lt;endl;\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\ncout&lt;&lt;"请输入学生人数：";\r\ncin&gt;&gt;nStuNum;\r\nsystem("cls"); //清空屏幕内容\r\nint i;\r\nfor(i=0;i&lt;nStuNum;i++)\r\n{\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"请输入学生["&lt;&lt;i+1&lt;&lt;"]的成绩："&lt;&lt;endl;\r\ncout&lt;&lt;" 姓名："; cin&gt;&gt;sStu[i].cName;\r\ncout&lt;&lt;" 学号："; cin&gt;&gt;sStu[i].cStuNum;\r\ncout&lt;&lt;endl;\r\ncout&lt;&lt;" 语文："; cin&gt;&gt;sStu[i].dChinese;\r\ncout&lt;&lt;" 数学："; cin&gt;&gt;sStu[i].dMath;\r\ncout&lt;&lt;" 英语："; cin&gt;&gt;sStu[i].dEnglish;\r\ncout&lt;&lt;" 编程："; cin&gt;&gt;sStu[i].dVC;\r\nsStu[i].dTotal=sStu[i].dChinese+sStu[i].dMath+sStu[i].dEnglish+sStu[i].dVC; //计算总成绩\r\nsStu[i].dAvg=sStu[i].dTotal/4.0; //计算平均成绩\r\ncout&lt;&lt;endl;\r\n}\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"成绩已经输入系统，3秒后返回主菜单"&lt;&lt;endl;\r\nsystem("ping 127.0.0.1 -n 3 &gt; NUL"); //利用ping进行延迟\r\nfSHOW_MENU(); //调用菜单函数显示菜单\r\n}\r\nvoid fSORT() //排序学生成绩\r\n{\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\ncout&lt;&lt;" 排序学生平均成绩"&lt;&lt;endl;\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\nint i,j;\r\nSTUDENT sTemp;\r\nfor(i=0;i&lt;nStuNum-1;i++) //排序\r\nfor(j=i+1;j&lt;nStuNum;j++)\r\nif(sStu[i].dAvg&gt;sStu[j].dAvg)\r\n{\r\nsTemp=sStu[i];\r\nsStu[i]=sStu[j];\r\nsStu[j]=sTemp;\r\n}\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"成绩已排序，3秒后返回主菜单"&lt;&lt;endl;\r\nsystem("ping 127.0.0.1 -n 3 &gt; NUL"); //利用ping进行延迟\r\nfSHOW_MENU(); //调用菜单函数显示菜单\r\n}\r\nvoid fOUTPUT() //输出学生成绩\r\n{\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\ncout&lt;&lt;" 输出学生全部成绩"&lt;&lt;endl;\r\ncout&lt;&lt;"***********************************************"&lt;&lt;endl;\r\nint i;\r\ncout.width(11);\r\ncout&lt;&lt;"姓名";\r\ncout.width(15);\r\ncout&lt;&lt;"学号\\t";\r\ncout.width(0);\r\ncout&lt;&lt;"语文\\t"&lt;&lt;"数学\\t"&lt;&lt;"英语\\t"&lt;&lt;"编程\\t"&lt;&lt;"平均\\t"&lt;&lt;"总分"&lt;&lt;endl;\r\nfor(i=0;i&lt;nStuNum;i++)\r\n{\r\ncout.width(13);\r\ncout&lt;&lt;sStu[i].cName;\r\ncout.width(11);\r\ncout&lt;&lt;sStu[i].cStuNum&lt;&lt;"\\t";\r\ncout.width(0);\r\ncout&lt;&lt;sStu[i].dChinese&lt;&lt;"\\t"&lt;&lt;sStu[i].dMath&lt;&lt;"\\t"&lt;&lt;sStu[i].dEnglish&lt;&lt;"\\t"&lt;&lt;sStu[i].dVC&lt;&lt;"\\t"&lt;&lt;sStu[i].dAvg&lt;&lt;"\\t"&lt;&lt;sStu[i].dTotal&lt;&lt;endl;\r\n}\r\ncout&lt;&lt;"\\n*按回车键返回主菜单"&lt;&lt;endl;\r\ngetchar();\r\nfSHOW_MENU(); //调用菜单函数显示菜单\r\n}\r\nvoid fEXIT()\r\n{\r\nsystem("cls"); //清空屏幕内容\r\ncout&lt;&lt;"Bye-bye!"&lt;&lt;endl;\r\nsystem("ping 127.0.0.1 -n 3 &gt; NUL"); //利用ping进行延迟\r\nexit(0); //强制终止\r\n}\r\nvoid main()\r\n{\r\nfSHOW_MENU(); //调用菜单函数显示菜单\r\n}\r\n\r\n&nbsp;', 'StudentsScore.cpp', '', 'publish', 'open', 'open', '', 'studentsscore-cpp', '', '', '2013-03-17 11:03:51', '2013-03-17 02:03:51', '', 0, 'http://www.fcsys.tk/webapp/wordpress/?p=207', 0, 'post', '', 5),
