<?php
// Setting and Options
$uplinkDns = '119.29.29.29';
$uplinkGeoCN = 'http://ip.chinaz.com/?ip=';
$uplinkGeoEN = 'http://ip.cn/index.php?ip=';

// Global
$ipcnCache = '';

// Get client IP address
function getIPAddr() {
	$ip = $_SERVER["HTTP_X_FORWARDED_FOR"];
	if($ip != "") {
		$arr = explode(",",$ip);
		return $arr[0];
	} else {
		return $_SERVER["REMOTE_ADDR"];
	}
}

// Http request process module
function httpReq($url) {
	$ch = curl_init($url);
	curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
	curl_setopt($ch, CURLOPT_TIMEOUT, 5);
	curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/47.0.2526.106 Safari/537.36');
	$r = curl_exec($ch);
	curl_close($ch);
	return $r;
}

// DNS domain request module
function dnsReq($uplinkDns, $domain, $ipaddr) {
    $r = httpReq('http://'.$uplinkDns.'/d?dn='.$domain.'&ip='.$ipaddr);
    if(strpos($r,';') > 0) {
        // Multiple IP result
        $rs = explode(';',$r);
        return trim($rs[0]);
    } else {
        // Single IP result
        return trim($r);
    }
}

// Get IP GEO Position
function getIPGeo($ipaddr, $uplink, $mode) {
    global $ipcnCache;
    switch ($mode) {
        case 'CN': 
            $r = httpReq($uplink.$ipaddr);
            preg_match_all('/Whwtdhalf w50-0">(.*)<\/span>/', $r, $rs);
            switch(explode(" ",$rs[1][1])[1]) {
                case '电信': return str_replace("电信","中国电信 光纤/宽带/手机/专线", $rs[1][1]);
                case '联通': return str_replace("联通","中国联通 宽带/光纤/手机/专线", $rs[1][1]);
                case '移动': return str_replace("移动","中国移动 手机/宽带/光纤/专线", $rs[1][1]);
                case '铁通': return str_replace("铁通","中国铁通 光纤/宽带/专线", $rs[1][1]);
                case '电信通': return str_replace("电信通","电信通 宽带/光纤/专线", $rs[1][1]);
                case '有线通': return str_replace("有线通","有线通 宽带/光纤/专线", $rs[1][1]);
                default: return $rs[1][1];
            }
        case 'EN': 
            $ipcnCache = $r = httpReq($uplink.$ipaddr);
            preg_match_all('/GeoIP: (.*)<\/p>/', $r, $rs);
            return $rs[1][0];
    }
}

// Main Entry
if(!isset($_GET['ip']) || $_GET['ip'] == NULL) {
    $modeText = '当前';
    $modeIP = getIPAddr();
} else {
    $modeText = '查询';
    // Is URL or Domain?
    if(strpos($_GET['ip'], '/') > -1)
        $domain = parse_url($_GET['ip'])['host'];
    else
        $domain = $_GET['ip'];
    // Is valid IP address
    if(filter_var($domain, FILTER_VALIDATE_IP)) {
        // Valid
        $modeIP = $domain;
    } else {
        // NOT Valid
        $modeIP = dnsReq($uplinkDns, $domain, getIPAddr());
    }
} 
$modeGeoCN = getIPGeo($modeIP, $uplinkGeoCN, 'CN');

// Is from browser or cURL?
if(!isset($_GET['mode']) && strpos($_SERVER['HTTP_USER_AGENT'], 'Mozilla') > -1) {
