using namespace std;
#include "stdafx.h"
#include&lt;iostream.h&gt;
#include&lt;stdlib.h&gt;
#include&lt;windows.h&gt;

typedef struct //学生类型结构体变量
{
char cName[12];
char cStuNum[10];
double dChinese,dMath,dEnglish,dVC;
double dAvg,dTotal;
}STUDENT;

int nStuNum=0; //学生人数
STUDENT sStu[150]; //学生结构体数组

void fINPUT(); //声明表列
void fSORT();
void fOUTPUT();
void fEXIT();

void fSHOW_MENU() //菜单函数
{
__MENUSTART__:
system("cls"); //清空屏幕内容
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
cout&lt;&lt;" 欢迎使用学生成绩管理系统"&lt;&lt;endl;
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
cout&lt;&lt;" [1] 输入学生各科成绩"&lt;&lt;endl;
cout&lt;&lt;" [2] 排序学生平均成绩"&lt;&lt;endl;
cout&lt;&lt;" [3] 输出学生最终成绩"&lt;&lt;endl;
cout&lt;&lt;" [-] ----------------"&lt;&lt;endl;
cout&lt;&lt;" [4] 退出学生成绩系统"&lt;&lt;endl;
int nMenu_Item=0;
cout&lt;&lt;"\n*请选择一项操作以继续[1-4]：";
cin&gt;&gt;nMenu_Item; //用户输入选项
switch(nMenu_Item) //判断用户输入并调用相应函数
{
case 1:fINPUT();break;
case 2:fSORT();break;
case 3:fOUTPUT();break;
case 4:fEXIT();break;
default:goto __MENUSTART__; //若不对重新显示菜单
}
}
void fINPUT() //输入学生成绩
{
system("cls"); //清空屏幕内容
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
cout&lt;&lt;" 输入学生各科成绩"&lt;&lt;endl;
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
cout&lt;&lt;"请输入学生人数：";
cin&gt;&gt;nStuNum;
system("cls"); //清空屏幕内容
int i;
for(i=0;i&lt;nStuNum;i++)
{
system("cls"); //清空屏幕内容
cout&lt;&lt;"请输入学生["&lt;&lt;i+1&lt;&lt;"]的成绩："&lt;&lt;endl;
cout&lt;&lt;" 姓名："; cin&gt;&gt;sStu[i].cName;
cout&lt;&lt;" 学号："; cin&gt;&gt;sStu[i].cStuNum;
cout&lt;&lt;endl;
cout&lt;&lt;" 语文："; cin&gt;&gt;sStu[i].dChinese;
cout&lt;&lt;" 数学："; cin&gt;&gt;sStu[i].dMath;
cout&lt;&lt;" 英语："; cin&gt;&gt;sStu[i].dEnglish;
cout&lt;&lt;" 编程："; cin&gt;&gt;sStu[i].dVC;
sStu[i].dTotal=sStu[i].dChinese+sStu[i].dMath+sStu[i].dEnglish+sStu[i].dVC; //计算总成绩
sStu[i].dAvg=sStu[i].dTotal/4.0; //计算平均成绩
cout&lt;&lt;endl;
}
system("cls"); //清空屏幕内容
cout&lt;&lt;"成绩已经输入系统，3秒后返回主菜单"&lt;&lt;endl;
system("ping 127.0.0.1 -n 3 &gt; NUL"); //利用ping进行延迟
fSHOW_MENU(); //调用菜单函数显示菜单
}
void fSORT() //排序学生成绩
{
system("cls"); //清空屏幕内容
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
cout&lt;&lt;" 排序学生平均成绩"&lt;&lt;endl;
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
int i,j;
STUDENT sTemp;
for(i=0;i&lt;nStuNum-1;i++) //排序
for(j=i+1;j&lt;nStuNum;j++)
if(sStu[i].dAvg&gt;sStu[j].dAvg)
{
sTemp=sStu[i];
sStu[i]=sStu[j];
sStu[j]=sTemp;
}
system("cls"); //清空屏幕内容
cout&lt;&lt;"成绩已排序，3秒后返回主菜单"&lt;&lt;endl;
system("ping 127.0.0.1 -n 3 &gt; NUL"); //利用ping进行延迟
fSHOW_MENU(); //调用菜单函数显示菜单
}
void fOUTPUT() //输出学生成绩
{
system("cls"); //清空屏幕内容
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
cout&lt;&lt;" 输出学生全部成绩"&lt;&lt;endl;
cout&lt;&lt;"***********************************************"&lt;&lt;endl;
int i;
cout.width(11);
cout&lt;&lt;"姓名";
cout.width(15);
cout&lt;&lt;"学号\t";
cout.width(0);
cout&lt;&lt;"语文\t"&lt;&lt;"数学\t"&lt;&lt;"英语\t"&lt;&lt;"编程\t"&lt;&lt;"平均\t"&lt;&lt;"总分"&lt;&lt;endl;
for(i=0;i&lt;nStuNum;i++)
{
cout.width(13);
cout&lt;&lt;sStu[i].cName;
cout.width(11);
cout&lt;&lt;sStu[i].cStuNum&lt;&lt;"\t";
cout.width(0);
cout&lt;&lt;sStu[i].dChinese&lt;&lt;"\t"&lt;&lt;sStu[i].dMath&lt;&lt;"\t"&lt;&lt;sStu[i].dEnglish&lt;&lt;"\t"&lt;&lt;sStu[i].dVC&lt;&lt;"\t"&lt;&lt;sStu[i].dAvg&lt;&lt;"\t"&lt;&lt;sStu[i].dTotal&lt;&lt;endl;
}
cout&lt;&lt;"\n*按回车键返回主菜单"&lt;&lt;endl;
getchar();
fSHOW_MENU(); //调用菜单函数显示菜单
}
void fEXIT()
{
system("cls"); //清空屏幕内容
cout&lt;&lt;"Bye-bye!"&lt;&lt;endl;
system("ping 127.0.0.1 -n 3 &gt; NUL"); //利用ping进行延迟
exit(0); //强制终止
}
void main()
{
fSHOW_MENU(); //调用菜单函数显示菜单
}

